package com.example.fragments

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.appcompat.app.AppCompatActivity
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.adapters.MoviesAdapter
import com.example.models.Movie
import com.example.myapplication.R




class MostRecentFragment : Fragment() {
    private lateinit var myAdapter: MoviesAdapter
    private lateinit var recyclerView: RecyclerView
    private lateinit var moviesList : MutableList<Movie>

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)

    }

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        // Inflate the layout for this fragment


        val viewer =  inflater.inflate(R.layout.fragment_most_recent, container, false)
        recyclerView= viewer.findViewById<RecyclerView>(R.id.movieRecyclerView)
        initRecyclerView()
        return viewer
    }

    private fun initRecyclerView() {
        moviesList = ArrayList()
        moviesList.add(
            Movie("data:image/jpeg;base64,","Despicable Me(2014)",8.2,8.6,80,false)
        )
        moviesList.add(
            Movie("data:image/jpeg;base64,","Luca(2021)",7.7,8.2,91,false)
        )
        moviesList.add(
            Movie("data:image/jpeg;base64,","The Secret of Marrowbone",6.7,8.2,98,false)
        )
        moviesList.add(
            Movie("data:image/jpeg;base64,","Halloween Kills(2021)",6.7,8.8,98,false)
        )
        moviesList.add(
            Movie("data:image/jpeg;base64,","War for the Planet of the Apes(2015)",7.3,7.5,77,false)
        )

        myAdapter= MoviesAdapter(moviesList
        ) { position ->
            val myDataAtPosition = moviesList[position]
            myDataAtPosition.isBookmarked = !myDataAtPosition.isBookmarked
            myAdapter.notifyItemChanged(position)

        }
        recyclerView.layoutManager = LinearLayoutManager(context, LinearLayoutManager.VERTICAL,false)
        recyclerView.adapter=myAdapter
    }

}